import { useState, useEffect } from 'react'
import { Navigation } from './components/navigation'
import { Header } from './components/header'
import { Features } from './components/features'
import { About } from './components/about'
import { Services } from './components/services'
import { Gallery } from './components/gallery'
import { Testimonials } from './components/testimonials'
import { Team } from './components/Team'
import { Contact } from './components/contact'
import JsonData from './data/data.json'
import SmoothScroll from 'smooth-scroll'
// Google Analytics
import ReactGa from 'react-ga';
import { Route, Switch, Redirect } from 'react-router-dom'
import useGaTracker from './components/useGaTracker'


export const scroll = new SmoothScroll('a[href*="#"]', {
  speed: 1000,
  speedAsDuration: true,
})

const App = () => {
  const [landingPageData, setLandingPageData] = useState({})
  useEffect(() => {
    setLandingPageData(JsonData)
  }, [])


  // // Google Analytics
  // useEffect(() => {
  //   ReactGa.initialize('UA-201476891-1');

  //   // To report page view
  //   ReactGa.pageview('/#about');
  // }, [])

  return (
    <div>
<Router>
      <Switch>
          <Route path="/" ><Navigation/></Route>
          <Route path="/#page-top"> <Header data={landingPageData.Header} /> </Route>
          <Route path="/#about"><About data={landingPageData.About} /></Route>
          <Route path="/#services"><Services data={landingPageData.Services} /></Route>
          <Route path="/#testimonials"><Testimonials data={landingPageData.Testimonials} /></Route>
          <Route path="/#team"><Team data={landingPageData.Team} /></Route>
          <Route path="/#contact"><Contact data={landingPageData.Contact} /></Route>
       </Switch>

</Router>
    </div>
  )
}

export default App
